import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { LoginRequest } from '../models/login-request.interface';
import { SessionInformation } from '../models/session-information.interface';
import { RegisterRequest } from '../models/register-request.interface';
import { RefreshTokenRequest } from '../models/refresh-token-request.interface';
import { RefreshTokenResponse } from '../models/refresh-token-response.interface';

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  private pathService = 'api/auth';

  constructor(private httpClient: HttpClient) { }

  public register(registerRequest: RegisterRequest): Observable<null> {
    return this.httpClient.post<null>(`${this.pathService}/register`, registerRequest);
  }

  public login(loginRequest: LoginRequest): Observable<SessionInformation> {
    return this.httpClient.post<SessionInformation>(`${this.pathService}/login`, loginRequest);
  }

  public refreshToken(refreshTokenRequest: RefreshTokenRequest): Observable<RefreshTokenResponse> {
    return this.httpClient.post<RefreshTokenResponse>(`${this.pathService}/refreshToken`, refreshTokenRequest);
  }
}

import { Injectable } from '@angular/core';
import { SessionInformation } from '../models/session-information.interface';
import { RefreshTokenResponse } from '../models/refresh-token-response.interface';

const TOKEN_KEY = 'auth-token';
const TOKEN_TYPE_KEY = 'auth-token-type';
const REFRESH_TOKEN_KEY = 'refresh-token';

@Injectable({
  providedIn: 'root'
})
export class TokenStorageService {

  constructor() { }

  public getToken(): string | null {
    return window.localStorage.getItem(TOKEN_KEY);
  }

  public getTokenType(): string | null {
    return window.localStorage.getItem(TOKEN_TYPE_KEY);
  }

  public getRefreshToken(): string | null {
    return window.localStorage.getItem(REFRESH_TOKEN_KEY);
  }

  public saveToken(token: string) :void {
    window.localStorage.removeItem(TOKEN_KEY);
    window.localStorage.setItem(TOKEN_KEY, token);
  }

  public saveTokenType(tokenType: string) :void {
    window.localStorage.removeItem(TOKEN_TYPE_KEY);
    window.localStorage.setItem(TOKEN_TYPE_KEY, tokenType);
  }

  public saveRefreshToken(refreshToken: string) :void {
    window.localStorage.removeItem(REFRESH_TOKEN_KEY);
    window.localStorage.setItem(REFRESH_TOKEN_KEY, refreshToken);
  }

  public clearSessionInformation() :void {
    window.localStorage.removeItem(TOKEN_KEY);
    window.localStorage.removeItem(TOKEN_TYPE_KEY);
    window.localStorage.removeItem(REFRESH_TOKEN_KEY);
  }

  public saveSessionInformation(data : SessionInformation) :void {
    this.saveToken(data.token);
    this.saveTokenType(data.type);
    this.saveRefreshToken(data.refreshToken);
  }

  public saveTokenAfterRefresh(data: RefreshTokenResponse): void {
    this.clearSessionInformation();
    this.saveToken(data.token);
    this.saveTokenType(data.type);
    this.saveRefreshToken(data.refreshToken);
  }
}

export const environment = {
    serviceCacheMaxAgeMs: 600000 // 10 minutes before posts reloading
};

import { environment } from '../../../environments/environment';
import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { BehaviorSubject, map, Observable, of, shareReplay, switchMap } from 'rxjs';
import { User } from '../models/user.interface';
import { Subscription } from '../models/subscription.interface';
import { UpdateUserRequest } from '../models/update-user-request';

@Injectable({
  providedIn: 'root'
})
export class CurrentUserSubscriptionService {

  private apiPath = 'api/user';

  private subscriptions$: BehaviorSubject<Subscription[] |null> | null = null;
  private cachedAt: number = 0;
  private isReloading: boolean = false;
  
  constructor(private httpClient: HttpClient) { }

  private getSubscriptionSubject() : BehaviorSubject<Subscription[] | null> {
    if(this.subscriptions$ === null) {
      this.subscriptions$ = new BehaviorSubject<Subscription[] | null>(null);
    }
    return this.subscriptions$;
  }

  public shouldReload(): boolean {
    return !this.isReloading && new Date().getTime() - this.cachedAt > environment.serviceCacheMaxAgeMs;
  }
  
  public getCurrentUserSubscriptions() : Observable<Subscription[]>{
    return this.getSubscriptionSubject().pipe(
      switchMap((subscriptions: Subscription[] | null) => {
        if (subscriptions && !this.shouldReload()) {
          // send current subscriptions if already present
          return of(subscriptions);
        } else {
          // load from backend otherwise
          this.isReloading = true;
          return this.httpClient.get<Subscription[]>(`${this.apiPath}/me/subscriptions`).pipe(
            switchMap((fetchedSubscriptions: Subscription[]) => {
              this.cachedAt = new Date().getTime();
              this.isReloading = false;
              this.getSubscriptionSubject().next(fetchedSubscriptions); // update BehaviorSubject
              return of(fetchedSubscriptions);
            })
          );
        }
      })
    );
  }

  subscribe(topicId: number): Observable<Subscription> {
    return this.httpClient.post<Subscription>(`api/topics/${topicId}/subscription`, '').pipe(
      map((t: Subscription) => {
        // do not populate local cache when it was empty because next subscriptions retrieval must make an API request
        if(this.getSubscriptionSubject().getValue() != null) {
          this.getSubscriptionSubject().next([...this.getSubscriptionSubject().getValue() as Subscription[], t]);
        }
        return t;
      }),
    );
  }

  unSubscribe(topicId: number): Observable<any> {
    return this.httpClient.delete<void>(`api/topics/${topicId}/subscription`).pipe(
      map(() => {
        // do not populate local cache when it was empty because next subscriptions retrieval must make an API request
        if(this.getSubscriptionSubject().getValue() != null) {
          this.getSubscriptionSubject().next(this.getSubscriptionSubject().getValue()!.filter(s => s.topic.id != topicId));
        }
        return of(null);
      }),
    );
  }

  public clearCache(): void {
    this.subscriptions$ = null;
  }
}

import { environment } from '../../../environments/environment';
import { Injectable } from '@angular/core';
import { BehaviorSubject, map, Observable, of, shareReplay, switchMap, tap } from 'rxjs';
import { Post } from '../models/post.interface';
import { HttpClient } from '@angular/common/http';
import { CreatePostRequest } from '../models/create-post-request.interface';
import { PostSort } from '../models/post-sort.interface';
import { CurrentUserService } from './current-user.service';
import { Subscription } from '../models/subscription.interface';
import { CurrentUserSubscriptionService } from './current-user-subscription.service';

@Injectable({
  providedIn: 'root'
})
export class PostService {

  private apiPath:string = 'api/posts';
  private posts$: BehaviorSubject<Post[] |null> | null = null;
  private userSubscriptions$: Observable<Subscription[] | null> | null = null;
  private cachedAt: number = 0;
  private isReloading: boolean = false;
  
  constructor(private httpClient: HttpClient, private subscriptionService: CurrentUserSubscriptionService) {
   }

   private getPostsSubject() : BehaviorSubject<Post[] | null> {
    // cache local cache to force reload on further posts retrieval when current user subscriptions get updated
    if(this.userSubscriptions$ === null) {
      this.userSubscriptions$ = this.subscriptionService.getCurrentUserSubscriptions().pipe(tap((s) => {        
        if(this.posts$ !== null) {
          this.clearCache();
        }
      }));
      this.userSubscriptions$.subscribe();
    }

    if(this.posts$ === null) {
      this.posts$ = new BehaviorSubject<Post[] | null>(null);
    }
    return this.posts$;
  }

  clearCache(): void {
    this.posts$ = null;
  }

  /**
   * Sort posts according to sort data
   * @param posts 
   * @param sortData 
   * @returns sorted posts
   */
  sortPosts(posts: Post[], sortData?: PostSort): Post[] {
    if(!sortData) {
      return posts;
    }
    let sortedPosts: Post[] = posts;

    // author + post creation date
      return sortedPosts.sort((a, b) => {
        let authorOrder, postOrder = 0;
        if(sortData.sortByAuthor) {
          authorOrder = a.author.username.localeCompare(b.author.username);
          if(sortData.orderByAuthorAscending === false) {
            authorOrder = (authorOrder === -1 ? 1 : authorOrder === 0 ? 0 : -1);
          }
        }

        // no need to check if user explicitly asked to sort by post creation, as it must always be used for sorting
        // event when user asked for a sort by author  
        // if(sortData.sortByPost) {
          postOrder = a.createdAt.localeCompare(b.createdAt);
          if(sortData.orderByPostAscending === undefined || sortData.orderByPostAscending === false) {
            postOrder = (postOrder === -1 ? 1 : postOrder === 0 ? 0 : -1);
          }
        // }
        return authorOrder || postOrder;
      });
  }

  public shouldReload(): boolean {
    return !this.isReloading && new Date().getTime() - this.cachedAt > environment.serviceCacheMaxAgeMs;
  }

  /**
   * Retrieves the sorted posts after reloading them from the backend if needed
   * @returns the posts 
   */
  getAllPosts(sortData?: PostSort): Observable<Post[]> {
    // map posts to null when no posts present or reloading needed
    return this.getPostsSubject().pipe(
      switchMap((posts: Post[] | null) => {
        if (posts && !this.shouldReload()) {
          // send current posts if already present
          return of(this.sortPosts(posts, sortData));
        } else {
          this.isReloading = true;
          return this.httpClient.get<Post[]>(`${this.apiPath}`).pipe(
            switchMap((fetchedPosts: Post[]) => {
              this.cachedAt = new Date().getTime();
              this.isReloading = false;
              this.getPostsSubject().next(fetchedPosts); // update BehaviorSubject
              return of(fetchedPosts);
            })
          );
        }
      }));
  }

  getPostById(postId: string): Observable<Post> {
    return this.httpClient.get<Post>(`${this.apiPath}/${postId}`);
  }

  createPost(createPostRequest: CreatePostRequest) :Observable<Post> {
    return this.httpClient.post<Post>(`${this.apiPath}`, createPostRequest).pipe(
      map((p: Post) => {
        if(this.getPostsSubject().getValue() != null) {
          this.getPostsSubject().next([p, ...this.getPostsSubject().getValue() as Post[]]);
        }
        return p;
      }),
    );
  }
}

import { environment } from '../../../environments/environment';
import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { BehaviorSubject, map, Observable, of, shareReplay, switchMap } from 'rxjs';
import { User } from '../models/user.interface';
import { Subscription } from '../models/subscription.interface';
import { UpdateUserRequest } from '../models/update-user-request';

@Injectable({
  providedIn: 'root'
})
export class CurrentUserService {

  private apiPath = 'api/user';

  private user$: BehaviorSubject<User | null> | null = null;
  private cachedAt: number = 0;
  private isReloading: boolean = false;
  
  constructor(private httpClient: HttpClient) { }

  private getUserSubject() : BehaviorSubject<User | null> {
    if(this.user$ === null) {
      this.user$ = new BehaviorSubject<User | null>(null);
    }
    return this.user$;
  }

  public shouldReload(): boolean {
    return !this.isReloading && new Date().getTime() - this.cachedAt > environment.serviceCacheMaxAgeMs;
  }

  public getCurrentUser() : Observable<User> {
    
    return this.getUserSubject().pipe(
      switchMap((user: User | null) => {
        if (user && !this.shouldReload()) {
          // send current user if already present
          return of(user);
        } else {
          // load from backend otherwise
          this.isReloading = true;
          return this.httpClient.get<User>(`${this.apiPath}/me`).pipe(
            switchMap((fetchedUser: User) => {
              this.cachedAt = new Date().getTime();
              this.isReloading = false;
              this.getUserSubject().next(fetchedUser); // update BehaviorSubject
              return of(fetchedUser);
            })
          );
        }
      })
    );
  }

  public updateCurrentUser(updateUserRequest: UpdateUserRequest): Observable<User> {
    // load from backend otherwise
    return this.httpClient.put<User>(`${this.apiPath}/me`, updateUserRequest).pipe(
      switchMap((updatedUser: User) => {
        this.cachedAt = new Date().getTime();
        this.isReloading = false;
        this.getUserSubject().next(updatedUser); // reset BehaviorSubject
        return of(updatedUser);
      })
    );
  }
  public clearCache(): void {
    this.user$ = null;
  }
}

